import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './DoctorRegistration.css';

const DoctorRegistration = () => {
  const [formData, setFormData] = useState({
    hospitalId: '',
    doctorName: '',
    specialization: '',
    email: '', // Added email field
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/register-doctors', {
        hospitalId: formData.hospitalId,
        doctors: [{
          name: formData.doctorName,
          specialization: formData.specialization,
          email: formData.email
        }]
      });

      if (response.status === 200) {
        alert('Doctor registered successfully!');
        navigate('/consultation');
      } else {
        alert('Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('There was an error during registration!', error);
      alert('Registration failed due to a server error.');
    }
  };

  return (
    <div className="container">
      <h2>Register a Doctor</h2>
      <form onSubmit={handleSubmit} className="form">
        <input 
          type="text" 
          name="hospitalId" 
          placeholder="Hospital ID" 
          value={formData.hospitalId}
          onChange={handleChange}
          required 
        />
        <input 
          type="text" 
          name="doctorName" 
          placeholder="Doctor Name" 
          value={formData.doctorName}
          onChange={handleChange}
          required 
        />
        <input 
          type="text" 
          name="specialization" 
          placeholder="Specialization" 
          value={formData.specialization}
          onChange={handleChange}
          required 
        />
        <input 
          type="email" 
          name="email" 
          placeholder="Email" 
          value={formData.email}
          onChange={handleChange}
          required 
        />
        <button type="submit" className="submit-button">Register Doctor</button>
      </form>
    </div>
  );
}

export default DoctorRegistration;
