import React, { useState } from 'react';
import axios from 'axios';
import './DoctorRegistration.css';

const DoctorRegistration = () => {
  const [doctorCount, setDoctorCount] = useState(1);
  const [doctorData, setDoctorData] = useState([{ name: '', specialization: '' }]);

  const addDoctorFields = () => {
    setDoctorCount(doctorCount + 1);
    setDoctorData([...doctorData, { name: '', specialization: '' }]);
  };

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    const updatedDoctorData = [...doctorData];
    updatedDoctorData[index][name] = value;
    setDoctorData(updatedDoctorData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/register', doctorData);
      alert('Doctors registered successfully');
      setDoctorData([{ name: '', specialization: '' }]); // Clear form or redirect if needed
    } catch (error) {
      alert('Error registering doctors');
      console.error('There was an error registering the doctors!', error);
    }
  };

  const renderDoctorFields = () => {
    return doctorData.map((doctor, index) => (
      <fieldset key={index}>
        <legend>Doctor {index + 1}</legend>
        <div className="input-group">
          <label htmlFor={`doctorName${index}`}>Doctor's Name:</label>
          <input
            type="text"
            id={`doctorName${index}`}
            name="name"
            className="doctor-input"
            placeholder="Doctor Name"
            value={doctor.name}
            onChange={(e) => handleInputChange(index, e)}
            required
          />
          <label htmlFor={`specialization${index}`}>Specialization:</label>
          <select
            id={`specialization${index}`}
            name="specialization"
            className="doctor-input"
            value={doctor.specialization}
            onChange={(e) => handleInputChange(index, e)}
            required
          >
            <option value="">Select</option>
            <option value="cardiology">Cardiology</option>
            <option value="neurology">Neurology</option>
            <option value="orthopedics">Orthopedics</option>
            <option value="pediatrics">Pediatrics</option>
            <option value="general">General</option>
            <option value="dermatology">Dermatology</option>
            <option value="gynecology">Gynecology</option>
            <option value="psychiatry">Psychiatry</option>
          </select>
        </div>
      </fieldset>
    ));
  };

  return (
    <div className="container">
      <h2>Doctor Registration</h2>
      <form onSubmit={handleSubmit}>
        <div id="doctorContainer">
          {renderDoctorFields()}
        </div>
        <button type="button" onClick={addDoctorFields} className="add-button">
          Add Another Doctor
        </button>
        <button type="submit" className="submit-button">
          Save
        </button>
      </form>
    </div>
  );
};

export default DoctorRegistration;
