import React, { useState } from 'react';
import './DoctorRegistration.css';

const DoctorRegistration = () => {
  const [doctorCount, setDoctorCount] = useState(1);

  const addDoctorFields = () => {
    setDoctorCount(doctorCount + 1);
  };

  const renderDoctorFields = () => {
    const fields = [];
    for (let i = 1; i <= doctorCount; i++) {
      fields.push(
        <fieldset key={i}>
          <legend>Doctor {i}</legend>
          <div className="input-group">
            <label htmlFor={`doctorName${i}`}>Doctor's Name:</label>
            <input 
              type="text" 
              id={`doctorName${i}`} 
              name={`doctorName${i}`} 
              className="doctor-input" 
              placeholder="Doctor Name" 
              required 
            />
            <label htmlFor={`specialization${i}`}>Specialization:</label>
            <select 
              id={`specialization${i}`} 
              name={`specialization${i}`} 
              className="doctor-input" 
              required
            >
              <option value="">Select</option>
              <option value="cardiology">Cardiology</option>
              <option value="neurology">Neurology</option>
              <option value="orthopedics">Orthopedics</option>
              <option value="pediatrics">Pediatrics</option>
              <option value="general">General</option>
              <option value="dermatology">Dermatology</option>
              <option value="gynecology">Gynecology</option>
              <option value="psychiatry">Psychiatry</option>
            </select>
          </div>
        </fieldset>
      );
    }
    return fields;
  };

  return (
    <div className="container">
      <h2>Doctor Registration</h2>
      <form id="doctorRegistrationForm" action="doctor.php" method="POST">
        <div id="doctorContainer">
          {renderDoctorFields()}
        </div>
        <input type="hidden" id="doctorCount" name="doctorCount" value={doctorCount} />
        <button type="button" onClick={addDoctorFields} className="add-button">
          Add Another Doctor
        </button>
        <button type="submit" className="submit-button">
          Save
        </button>
      </form>
    </div>
  );
}

export default DoctorRegistration;
