import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './register.css';
import Header from './Header';

const Register = () => {
  const [formData, setFormData] = useState({
    type: 'User', // default selection
    name: '',
    contactNumber: '',
    email: '',
    address: '',
    password: ''
  });

  const [placeholders, setPlaceholders] = useState({
    name: 'Name',
    contactNumber: 'Contact Number',
    email: 'E-mail',
    address: 'Address',
    password: 'Password'
  });

  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Update placeholders based on selected type
    switch (formData.type) {
      case 'Hospital':
        setPlaceholders({
          name: 'Hospital Name',
          contactNumber: 'Hospital Contact Number',
          email: 'Hospital Email',
          address: 'Hospital Address',
          password: 'Password'
        });
        break;
      case 'Doctor':
        setPlaceholders({
          name: 'Doctor Name',
          contactNumber: 'Doctor Contact Number',
          email: 'Doctor Email',
          address: 'Doctor Address',
          password: 'Password'
        });
        break;
      case 'User':
      default:
        setPlaceholders({
          name: 'User Name',
          contactNumber: 'User Contact Number',
          email: 'User Email',
          address: 'User Address',
          password: 'Password'
        });
        break;
    }
  }, [formData.type]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/register', formData);

      if (response.status === 201) {
        alert('Registration successful!');
        navigate('/login');
      } else {
        setError(response.data.message || 'Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('There was an error registering!', error);
      setError('Registration failed due to a server error.');
    }
  };

  return (
    <div className="container">
      <Header />
      <div className="heading">Register</div>
      <form onSubmit={handleSubmit} className="form">
        <select 
          name="type" 
          value={formData.type} 
          onChange={handleChange} 
          className="input"
        >
          <option value="User">User</option>
          <option value="Hospital">Hospital</option>
          <option value="Doctor">Doctor</option>
        </select>
        <input 
          type="text" 
          className="input" 
          name="name" 
          placeholder={placeholders.name} 
          value={formData.name}
          onChange={handleChange}
          required 
        />
        <input 
          type="tel" 
          className="input" 
          name="contactNumber" 
          placeholder={placeholders.contactNumber} 
          value={formData.contactNumber}
          onChange={handleChange}
          required 
        />
        <input 
          type="email" 
          className="input" 
          name="email" 
          placeholder={placeholders.email} 
          value={formData.email}
          onChange={handleChange}
          required 
        />
        <input 
          type="text" 
          className="input" 
          name="address" 
          placeholder={placeholders.address} 
          value={formData.address}
          onChange={handleChange}
          required 
        />
        <input 
          type="password" 
          className="input" 
          name="password"  
          placeholder={placeholders.password} 
          value={formData.password}
          onChange={handleChange}
          required 
        />
        <input 
          className="login-button" 
          type="submit" 
          value="Register" 
        />
        {error && <p className="error-message">{error}</p>}
      </form>
      <span className="agreement">
        <a href="#">Learn user licence agreement</a>
      </span>
    </div>
  );
}

export default Register;
