import React, { useState } from 'react';
import axios from 'axios';
import './DoctorRegistration.css';

const DoctorRegistration = () => {
  const [doctorData, setDoctorData] = useState({
    hospitalEmail: '',
    name: '',
    specialization: '',
    email: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setDoctorData({
      ...doctorData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/registerDoctor', doctorData);
      if (response.status === 200) {
        alert('Doctor registered successfully');
        setDoctorData({
          hospitalEmail: '',
          name: '',
          specialization: '',
          email: ''
        }); // Clear form or redirect if needed
      } else {
        alert('Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('There was an error registering the doctor!', error);
      alert('Error registering doctor');
    }
  };

  return (
    <div className="container">
      <h2>Doctor Registration</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="email"
          name="hospitalEmail"
          placeholder="Hospital Email"
          value={doctorData.hospitalEmail}
          onChange={handleInputChange}
          required 
        />
        <input 
          type="text"
          name="name"
          placeholder="Doctor's Name"
          value={doctorData.name}
          onChange={handleInputChange}
          required 
        />
        <select 
          name="specialization"
          value={doctorData.specialization}
          onChange={handleInputChange}
          required
        >
          <option value="">Select Specialization</option>
          <option value="cardiology">Cardiology</option>
          <option value="neurology">Neurology</option>
          <option value="orthopedics">Orthopedics</option>
          <option value="pediatrics">Pediatrics</option>
          <option value="general">General</option>
          <option value="dermatology">Dermatology</option>
          <option value="gynecology">Gynecology</option>
          <option value="psychiatry">Psychiatry</option>
        </select>
        <input 
          type="email"
          name="email"
          placeholder="Doctor's Email"
          value={doctorData.email}
          onChange={handleInputChange}
          required 
        />
        <button type="submit">Register Doctor</button>
      </form>
    </div>
  );
};

export default DoctorRegistration;
